sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
     var formVal = g_form.getValue('resource_unit_number');
    var ga = new GlideAjax('currentRuCount');
    ga.addParam('sysparm_name', 'getCmdbCount');
    ga.addParam('sysparm_ru_name', formVal);
    ga.getXML(countParse);

    function countParse(response) {

        var answer = JSON.parse(response.responseXML.documentElement.getAttribute('answer'));

			//g_form.addInfoMessage(answer);
			g_form.setValue("u_ru_description", answer.description);
			g_form.setValue("u_arc_rrc_rate", answer.arcrate);
			g_form.setValue("u_baseline", answer.baseline);
		}
}
