sys_script_include.script:
var currentRuCount = Class.create();
currentRuCount.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getCmdbCount: function() {
        var rudef = {};
        var cmdbCount = new GlideRecord('x_323515_account_0_ru_definition');
        cmdbCount.addQuery('sys_id', this.getParameter("sysparm_ru_name"));
        cmdbCount.query();
        while (cmdbCount.next()) {

            rudef.count = cmdbCount.current_count.toString();
            rudef.arcrate = cmdbCount.arc_rrc_rate.toString();
            rudef.baseline = cmdbCount.baseline.toString();
            rudef.description = cmdbCount.getDisplayValue("description");
            gs.info(rudef);
        }
        return JSON.stringify(rudef);

    },
    type: 'currentRuCount'
});
