sys_script_include.script:
var GenerateRuPDF = Class.create();
GenerateRuPDF.prototype = {
    initialize: function() {
    },
    generate: function(pdf_data) {
        var meta_tags = '';
		var css = '';
		var header = '';
		var footer = '';
		var body = '';
		var body_data = '';
		var ru_labels = [];
		var rus = [];
		var pdf_url = '';
		var has_report_temp = false;
		var deleted_report_temp = false;
		var no_generate_pdf = '';
		var no_add_report_temp = '';

		var pdf_config = {
			html: '',
			table_target: 'x_323515_account_0_financial_statement',
			sys_id_target: '',
			pdf_name: this.create_pdf_name(),
			header_footer_info: {
				PageSize: 'A4 ',
				PageOrientation: 'LANDSCAPE',
				GeneratePageNumber: 'false',
				TopOrBottomMargin: '16',
				LeftOrRightMargin: '16'
			},

		};

        meta_tags = this.create_metatags(pdf_data.title);

		css = this.create_css();

		header = this.create_header();

		footer = this.create_footer();

		ru_labels = pdf_data.ru_labels;
		rus = pdf_data.rus;

		body = this.create_body(ru_labels);

		body_data = this.create_body_data(rus);

		pdf_config.html = meta_tags + css + header + body + body_data + footer;

		has_report_temp = this.has_report_temp(pdf_config);

		if (has_report_temp) {
			deleted_report_temp = this.delete_report_Temp(pdf_config);

			if (deleted_report_temp) {
				pdf_config.sys_id_target = this.add_report_temp(pdf_config);

				if (pdf_config.sys_id_target && pdf_config.sys_id_target !== '') {
					pdf = this.convert_html_to_pdf(pdf_config);

					if (pdf && pdf.attachment_id !== '') {
						pdf_url = this.create_pdf_url_to_download(pdf.attachment_id);

						return pdf_url;
					} else {
						no_generate_pdf = 'Could not generate report!';

						return no_generate_pdf;
					}


				} else {
					no_add_report_temp = 'It was not possible to generate the report, because the record was not added in the table <Report Temp>';

					return no_add_report_temp;
				}

			} else {
				var no_deleted_report_temp = 'Could not generate the report because it was not possible to delete the previous record in the table <Report Temp>';

				return no_deleted_report_temp;
			}

		} else {
			pdf_config.sys_id_target = this.add_report_temp(pdf_config);

			if (pdf_config.sys_id_target && pdf_config.sys_id_target !== '') {
				pdf = this.convert_html_to_pdf(pdf_config);

				if (pdf && pdf.attachment_id !== '') {
					pdf_url = this.create_pdf_url_to_download(pdf.attachment_id);

					return pdf_url;
				} else {
					no_generate_pdf = 'Could not generate report!';

					return no_generate_pdf;
				}
			} else {
				no_add_report_temp = 'It was not possible to generate the report, because the record in the <Report Temp> table was not added';

				return no_add_report_temp;
			}
		}
    },




	has_report_temp: function(pdf_config) {
		var pdf_name = pdf_config.pdf_name;
		var has_report_temp = false;

		var glide_record = new GlideRecord(pdf_config.table_target);

		glide_record.addQuery("u_name", pdf_name);
		glide_record.query();

		if (glide_record.next()) {
			has_report_temp = true;
		}

		return has_report_temp;
	},




	create_pdf_name: function() {
        var pdf_name = '';

        pdf_name = 'RU_Summary ' + gs.endOfThisMonth();

        return pdf_name;
    },




	create_metatags: function(page_html_title) {
        var meta_tags = '';

        meta_tags = '<!DOCTYPE html><html lang="en">' + 
			'<head>' + 
            '<meta charset="UTF-8" />' + 
            '<meta name="viewport" content="width=device-width, initial-scale=1.0" />' + 
            '<title>' + page_html_title + '</title>';

        return meta_tags;
    },




	create_css: function() {
        var css = '';

        css = '<style>' + 
            '@page { size: A4 landscape; margin-left: 1cm; margin-top: 1cm; margin-right: 1cm; margin-bottom: 1cm;' + 
            '@top-left { font-family: Roboto, sans-serif; font-size: 12px; content: element(page-header);text-align: left;}' +
			'@bottom-left { font-family: Roboto, sans-serif; font-size: 12px; content: element(page-footer);}' +
			'.page-header { position: running(page-header) ;}' +
			'.page-footer {position: running(page-footer);}'+
			'.current-page-number:after { content: counter(page) " of " counter(pages); }' +

            '.page-header { border-bottom: 0px !important; padding-bottom: 0px !important; }' +
			'.page-footer { border-top: 0px !important; padding-bottom: 60px !important; }' +
            '.page-header,page-footer, .page-body { font-family: Roboto; }' +
			'.page-body { font-size: 12px; } ' +
			'table { border-collapse: collapse; border-spacing: 0; font-size: 12px; font-weight: 250; }' +
            '.table-header { border: 0px solid rgb(0, 0, 0); box-sizing: border-box; -moz-box-sizing: border-box; -webkit-box-sizing: border-box;}' +
			'.logo { margin-left: 02px; margin-bottom: 0px; }' +
			'.title { padding: 25px; margin-left: 2px; font-size: 16px !important; }' +
			'.table-filters { padding: 6px; margin-top: 20px; border-top: 2px solid #000; border-bottom: 2px solid #000; font-weight: 600; text-align: center; }' +
            'table.table-data thead tr th { padding: 10px 6px; background-color: #F9423A; border-left: 2px solid #F9423A; border-right: 2px solid #C1C1C1; text-align: center;  font-weight: bold; color: #FFFFFF; border: 0px solid #000; }' +
            'table.table-data tbody tr td { padding: 4px 6px; text-align: center; border-style: groove; border-width: thin}' +
			'.table-data { border: 0px; }' +
            '.table-data-title-1, .table-data-title-2 { color: #FFF !important; }' +
			'.table-data-title-1 { background-color: #3075B4 !important; border-left: 2px solid #3075B4 !important; border-right: 2px solid #3075B4 !important; }' +
			'.table-data-title-2 { background-color: #3298FD !important; border-left: 2px solid #3298FD !important; border-right: 2px solid #3298FD !important; }' +
			'table-data-total { border: 2px solid #000; }' +
			'.font-bold { font-weight: bold; }' +
			'.full-width { width: 100%; }' +
			'.flex { display: flex; }' +
			'.flex-1 { flex: 1 }' +
			'.flex-2 { flex: 2 }' +
			'.left { text-align: left; }' +
			'.right { text-align: right; }'+
			'.center { text-align: center; }'+
			'.no-border { border: 0px !important; }' +
			'</style></head><body>';

		return css;
    },




	create_date_hour_issue: function() {
		var issue = {
			date: '',
			hour: ''
		};

		var glideDateTime = new GlideDateTime();

        getTime = glideDateTime.getTime();

		issue.date = glideDateTime.getDate().getByFormat('dd/MM/YYYY');
		issue.hour = getTime.getByFormat('hh:mm:ss');

		return issue;
	},




	create_header: function() {
		var header = '';

		var issue = this.create_date_hour_issue();

		header = '<div class="page-header">' +
			'<div class="table-header flex">' +
			'<div class="logo flex-1"><img src="x_323515_account_0.logo_sn.png" width="200" height="150"/></div>' +
			'<div class="title flex-2">' +
			//'<div class="flex"><div class="font-bold left">Resource Unit Summary</div></div>' +
			//'<div class="flex"><div class="flex-1 left"><strong>Issue: </strong>' + issue.date  + ' ' + issue.hour  + '</div></div>' +
			//'<div class="flex"><div class="flex-1 left"><strong>Page: </strong><span class="current-page-number"></span></div></div>' +
			'</div>' +
			'</div>' +
			'</div>';

		return header;
	},

	create_footer: function() {
		var footer = '';

		var issue = this.create_date_hour_issue();

		footer = '<div class="page-footer">' +
			//'<div class="table-header flex">' +
			//'<div class="logo flex-1"><img src="x_323515_account_0.logo_sn.png" width="200" height="150"/></div>' +
			//'<div class="title flex-2">' +
			'<div class="flex"><div class="font-bold left">Resource Unit Summary</div></div>' +
			'<div class="center"><div class="flex-1 "><strong>Page: </strong><span class="current-page-number"></span></div></div>' +
			'<div class="right"><strong>Issue: </strong>' + issue.date  + ' ' + issue.hour  + '</div>' +

			//'</div>' +
			//'</div>' +
			'</div>';

		return footer;
	},




	create_body: function(ru_labels) {
		var body = '';

		body = '<div class="page-body">' +
			'<table class="table-data" width="100%">' +
			'<thead>';

		body += '<tr>';
		for (var label in ru_labels) {
			body += '<th>' + ru_labels[label] + '</th>';
		}
		body += '</tr>';

		body += '</thead>';
		body += '<tbody>';

		return body;
	},




	create_body_data: function(rus) { gs.info('leandro' + JSON.stringify(rus));
		var body_data = '';
		var self  = this;

		rus.forEach(function(ru) {

			body_data += '<tr>';
			for (var property in ru) {
				body_data += '<td>' + ru[property] + '</td>';
			}
			body_data += '</tr>';
		});

		body_data += '</tbody></table></div></body></html>';

		return body_data;
	},



	formatter_number_to_brl: function(number) {
		var number_formatted = number;

		number_formatted = number_formatted.toString().match(/^-?\d+(?:\.\d{0,2})?/)[0];

		if (number_formatted.length >= 0 ) {
			var number_formatted_aux = number_formatted.replace(/\./g,",");
			number_formatted = number_formatted_aux.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ".");
		}

		return number_formatted;
	},



	delete_report_Temp: function(pdf_config) {
		var pdf_name = pdf_config.pdf_name;
		var deleted = false;

		var glide_record = new GlideRecord(pdf_config.table_target);

		glide_record.addQuery("u_name", pdf_name);
		glide_record.query();

		if (glide_record.next()) {
			deleted = glide_record.deleteRecord();
		}

		return deleted;
	},



	add_report_temp: function (pdf_config) {
		var report_temp_sys_id = '';

		var glide_record = new GlideRecord(pdf_config.table_target);

		glide_record.initialize();

		glide_record.u_name = pdf_config.pdf_name;

		glide_record.state = '1';

		report_temp_sys_id = glide_record.insert();

		return report_temp_sys_id;
	},



	convert_html_to_pdf: function(pdf_config) {
		var pdf_generation_api = new sn_pdfgeneratorutils.PDFGenerationAPI();

		var pdf = pdf_generation_api.convertToPDFWithHeaderFooter(
            pdf_config.html, 
            pdf_config.table_target, 
            pdf_config.sys_id_target, 
            pdf_config.pdf_name, 
            pdf_config.header_footer_info,
			pdf_config.sys_id_font_family
        );

		gs.info(JSON.stringify(pdf));

		return pdf;
	},




    type: 'GenerateRuPDF'
};
